import React from 'react'

function Nav(){
   return(

      <nav>
         <ul className="NavUl">
            {categories.map((category) => (
                <li className={`NavLi ${currentCategory.name === category.name && !contactSelected && 'navActive'}`} key={category.name}>
                  <span onClick={() => {setCurrentCategory(category);setContactSelected(false);}}>
                    {capitalizeFirstLetter(category.name)}
                  </span>
                </li>
              ))}
         </ul>
      </nav>

   );

}

export default Nav;
