import React from 'react'
import { capitalizeFirstLetter } from "../../utils/helpers";

function Nav(props){
   const {
      categories = [],
      setCurrentCategory,
      currentCategory,
      portfolioSelected,
      setPortfolioSelected
    } = props;
   return(

      <nav>
         <ul className="NavUl">
            {categories.map((category) => (
                <li className={`NavLi ${currentCategory.name === category.name && !portfolioSelected && 'navActive'}`} key={category.name}>
                  <span onClick={() => {setCurrentCategory(category);setPortfolioSelected(false);}}>
                    {capitalizeFirstLetter(category)}
                  </span>
                </li>
              ))}
         </ul>
      </nav>

   );

}

export default Nav;
